import React, { useState } from 'react';
import {useQuery, gql} from '@apollo/client'
import {useRouter} from 'next/router'
//import icon from material-ui
import {NotificationsNone} from '@material-ui/icons';
const OBTENER_USUARIO = gql`
    query obtenerUsuario{
        obtenerUsuario{
            id  
            nombre
            apellido
        }
    }
`

const Header = () => {
    //Routing 
    const router = useRouter();
    //Estado para mostrar notificaciones
    const [notificaciones, setNotificaciones] = useState(false);

    //Query de Apollo
    const {data, loading, error} = useQuery(OBTENER_USUARIO)
    console.log(data)
    console.log(loading)
    console.log(error)
    //Proteger que no accedamos a data antes de tener resultado
    if(loading) return null;

    //Si no hay datos
    if(!data){
        return router.push('/login')
    }
    //Destructuramos obtenerUsuario
    const {nombre, apellido} = data.obtenerUsuario;
    //Cerrar Sesión
    const cerrarSesion = () => {
        localStorage.removeItem('token')
        router.push('/login')
    }
    
    return ( 
        <div className='flex justify-between mb-6'>
                <p className='mr-2'>Hola {nombre} {apellido}</p>
                <div className='group flex flex-col w-10 h-8  bg-gray-200 hover:bg-gray-50 rounded' onClick={()=>setNotificaciones(!notificaciones)}>
                    <NotificationsNone />
                    1
                    <p className={notificaciones ? "hidden" : "w-10 h-10 bg-red-700"}>Child</p>
                </div>    
                
                <button 
                    type="button"
                    className='bg-blue-800 w-full sm:w-auto font-bold uppercase text-xs rounded py-1 px-2 text-white shadow-md'
                    onClick={()=>cerrarSesion()}
                >
                    Cerrar sesión
                </button>
           </div> 
    );
}
 
export default Header;
