import Layout from '../components/Layout'
import {gql, useQuery} from '@apollo/client'
import Producto from '../components/Producto';
import Link from 'next/link';
import { useMessages } from '../graphql/hooks';

const OBTENER_PRODUCTOS = gql`
    query obtenerProductos{
        obtenerProductos{
            id
            nombre
            precio
            existencia
        }
    }
`
const productos = () => {
    const {data, loading, error} = useQuery(OBTENER_PRODUCTOS);
    const {messages} = useMessages();
    console.log("Mensaje obtenido desde el server", messages)
    if (loading) return "cargando";
    console.log(data);
    return (
        <>
            <Layout>
                <h1 className = "text-2xl text-gray-800 font-light">Productos</h1>
                <Link href='/nuevoproducto'>
                    <a className='bg-blue-800 py-2 px-5 mt-5 inline-block text-white rounded text-sm hover:bg-gray-800 mb-3 uppercase font-bold'>Nuevo Producto</a>  
                </Link>
                <table className='table-auto shadow-md mt-10 w-full w-lg'>
                    <thead className='bg-gray-800'>
                    <tr className='text-white'>
                        <th className='w-1/5 py-2'>Nombre Producto</th>
                        <th className='w-1/5 py-2'>Cantidad Producto</th>
                        <th className='w-1/5 py-2'>Precio</th> 
                        <th className='w-1/5 py-2'>Eliminar</th>
                        <th className='w-1/5 py-2'>Editar</th>
                    </tr>
                    </thead>
        
                    <tbody className='bg-white'>
                    {
                        
                        data.obtenerProductos.map(producto => (
                        <Producto
                            key={producto.id}
                            producto = {producto}
                        />
                        ))
                        
                    }

            
                    
                    
                    </tbody>
                </table>
            </Layout>
        </>
    )
}
  
export default productos
