import React, { useState } from 'react';
import {useQuery, gql} from '@apollo/client'
import {useRouter} from 'next/router'
//import icon from material-ui
import {NotificationsNone} from '@material-ui/icons';
const OBTENER_USUARIO = gql`
    query obtenerUsuario{
        obtenerUsuario{
            id  
            nombre
            apellido
        }
    }
`

const Header = () => {
    //Routing 
    const router = useRouter();
    //Estado para mostrar notificaciones
    const [notificaciones, setNotificaciones] = useState(false);

    //Query de Apollo
    const {data, loading, error} = useQuery(OBTENER_USUARIO)
    console.log(data)
    console.log(loading)
    console.log(error)
    //Proteger que no accedamos a data antes de tener resultado
    if(loading) return null;

    //Si no hay datos
    if(!data){
        return router.push('/login')
    }
    //Destructuramos obtenerUsuario
    const {nombre, apellido} = data.obtenerUsuario;
    //Cerrar Sesión
    const cerrarSesion = () => {
        localStorage.removeItem('token')
        router.push('/login')
    }
    
    return ( 
        <div className='flex justify-between mb-6'>
                <p className='mr-2'>Hola {nombre} {apellido}</p>


                {/* <!-- This example requires Tailwind CSS v2.0+ --> */}
<div className="relative inline-block text-left">
  <div>
    <button type="button" className="inline-flex justify-center w-full rounded-md border border-gray-300 shadow-sm px-4 py-2 bg-white text-sm font-medium text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-gray-100 focus:ring-indigo-500" id="menu-button" aria-expanded="true" aria-haspopup="true">
      {/* Options
      <!-- Heroicon name: solid/chevron-down --> */}
      <svg className="-mr-1 ml-2 h-5 w-5" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor" aria-hidden="true">
        <path fill-rule="evenodd" d="M5.293 7.293a1 1 0 011.414 0L10 10.586l3.293-3.293a1 1 0 111.414 1.414l-4 4a1 1 0 01-1.414 0l-4-4a1 1 0 010-1.414z" clip-rule="evenodd" />
      </svg>
    </button>
  </div>

  {/* <!--
    Dropdown menu, show/hide based on menu state.

    Entering: "transition ease-out duration-100"
      From: "transform opacity-0 scale-95"
      To: "transform opacity-100 scale-100"
    Leaving: "transition ease-in duration-75"
      From: "transform opacity-100 scale-100"
      To: "transform opacity-0 scale-95"
  --> */}
  <div className={notificaciones ? "hidden" : 'flex flex-col h-10 w-10 position:relative'} role="menu" aria-orientation="vertical" aria-labelledby="menu-button" tabindex="-1">
    <div className="py-1" role="none">
      {/* <!-- Active: "bg-gray-100 text-gray-900", Not Active: "text-gray-700" --> */}
      <a href="#" className="text-gray-700 block px-4 py-2 text-sm" role="menuitem" tabindex="-1" id="menu-item-0">Account settings</a>
      <a href="#" className="text-gray-700 block px-4 py-2 text-sm" role="menuitem" tabindex="-1" id="menu-item-1">Support</a>
      <a href="#" className="text-gray-700 block px-4 py-2 text-sm" role="menuitem" tabindex="-1" id="menu-item-2">License</a>
      {/* <form method="POST" action="#" role="none">
        <button type="submit" class="text-gray-700 block w-full text-left px-4 py-2 text-sm" role="menuitem" tabindex="-1" id="menu-item-3">Sign out</button>
      </form> */}
    </div>
  </div>
</div>
                {/* <div className='flex flex-col w-10 h-8  bg-gray-200 hover:bg-gray-50 rounded' onClick={()=>setNotificaciones(!notificaciones)}>
                    <div className='flex flex-row w-10 h-8'>    
                        <NotificationsNone />
                        1
                    </div>
                    <div className={notificaciones ? "hidden" : 'flex flex-col h-10 w-10 position:relative'}>
                        <p className={"w-10 h-10 bg-red-700"}>Child</p>
                        <p className={"w-10 h-10 bg-red-700"}>Child</p>
                        <p className={"w-10 h-10 bg-red-700"}>Child</p>
                        <p className={"w-10 h-10 bg-red-700"}>Child</p>
                        <p className={"w-10 h-10 bg-red-700"}>Child</p>
                        <p className={"w-10 h-10 bg-red-700"}>Child</p>
               
                    </div>
                </div>     */}
                
                <button 
                    type="button"
                    className='bg-blue-800 w-full sm:w-auto font-bold uppercase text-xs rounded py-1 px-2 text-white shadow-md'
                    onClick={()=>cerrarSesion()}
                >
                    Cerrar sesión
                </button>
           </div> 
    );
}
 
export default Header;
